import { useState } from "react";
import { Container, ImageArea, WorkList } from "./styles";
import salesforce from '../../assets/salesforce.png' 

export function Work() {
    type Project = {
        img_url: string,
        title: string;
        category: string;
        year: string;
        description: string;
      };

    const [details, setDetails] = useState({
        img_url: '',
        title: '',
        category:'',
        year: '',
        description: ''
    })

    const projectDesc: { [key: string]: Project } = {
        'project1': {
            img_url: '../../assets/salesforce.png',
            title: 'FTL Intern @Salesforce',
            category:'Web Development <br/> test',
            year: '2023',
            description: 'lorem lorem'
        },
        'project2': {
            img_url: 'https://hips.hearstapps.com/hmg-prod/images/cute-cat-photos-1593441022.jpg?resize=1200:*',
            title: "Zumbi's Hell",
            category:'C# <br/> Unity',
            year: '2023',
            description: 'lorem lorem'
        }
    }

    function getId(e:any){
        const objId = e.target.id
        setDetails(projectDesc[objId])
    }
    function deleteId(e:any){
        setDetails({
            img_url: '',
            title: '',
            category:'',
            year: '',
            description: ''
    })
    }

    return(
        <Container>
            <ImageArea>
                <div className="selected-project-image">
                    <img src={details.img_url} alt="" />
                    <div id="general">
                        <h1>{details.title}</h1>
                        <div id="general-descriptions">
                            <div>
                                <h4>category</h4>
                            </div>
                            <div>
                                Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ipsam optio eum ullam illo et doloremque quibusdam temporibus. Vero sunt laborum hic alias temporibus voluptatibus adipisci dolorum culpa. Quae, asperiores laudantium.
                            </div>

                        </div>
                    </div>

                </div>
            </ImageArea>
            <WorkList>
                <h1>Projects and thoughts.</h1>
                <a href="">
                <div className="project" 
                    id="project1"
                    onMouseEnter={getId}
                    onMouseLeave={deleteId}
                    >
                    <h4>FTL Intern @Salesforce</h4>
                    <h5>Full-Stack web Development</h5>
                </div></a>

                <div className="project" 
                    id="project2"
                    onMouseEnter={getId}
                    onMouseLeave={deleteId}
                    >
                    <h4>3D Survival Game</h4>
                    <h5>C#, Unity, Game Dev</h5>
                </div>

                <div className="project" 
                    id="project3"
                    onMouseEnter={getId}
                    onMouseLeave={deleteId}
                    >
                    <h4>Activities Scheduler</h4>
                    <h5>Data Structures and Algorithms</h5>
                </div>

                <div className="project" 
                    id="project4"
                    onMouseEnter={getId}
                    onMouseLeave={deleteId}
                    >
                    <h4>134340</h4>
                    <h5>Creative Writing</h5>
                </div>

                <a href="">
                <div className="project" 
                    id="project5"
                    onMouseEnter={getId}
                    onMouseLeave={deleteId}
                    >
                    <h4>Forest Therapy @ NTU</h4>
                    <h5>Extra Course</h5>
                </div></a>

            </WorkList>
        </Container>
    )
}
