import { useState } from "react";
import { Container, ImageArea, WorkList } from "./styles";

export function Work() {
    const [imgId, setImgId] = useState('')

    function getId(e:any){
        const imageId = e.target.id
        setImgId(imageId)
    }
    function deleteId(e:any){
        setImgId('')
    }
    return(
        <Container>
            <ImageArea>
                <div className="selected-project-image">
                    <img src={imgId} alt="" />
                </div>
            </ImageArea>
            <WorkList>
                <h1>Projects and thoughts.</h1>
                <div className="project" 
                    id="https://hips.hearstapps.com/hmg-prod/images/cute-cat-photos-1593441022.jpg?resize=1200:*"
                    onMouseEnter={getId}
                    onMouseLeave={deleteId}
                    >
                    <h4>FTL Intern @Salesforce</h4>
                    <h5>Full-Stack web Development</h5>
                </div>
                <div className="project">
                    <h4>assignment 2</h4>
                    <h5>descrption 2</h5>
                </div>
            </WorkList>
        </Container>
    )
}
