import styled from "styled-components"


export const Container = styled.header`
    height: 10rem;
`;

export const Content = styled.div`
    max-width: 1120px;
    margin: 0 auto;

    padding: 1.5rem 1rem 0rem;
    display: flex;
    justify-content: center;
    align-items: center;


    h1{
        font-style: normal;
        font-weight: 900;
        font-size: 96px;
        color: var(--blue-dark);
    }

    b{
        color: var(--purple-light);
    }

`;
