import styled from "styled-components"


export const Container = styled.header`
    max-width: 1120px;
    margin: 50px auto;

    color: var(--blue-dark);

    h1, h2{
        font-style: normal;
        font-weight: 700;
        font-size: 40px;
    }

    img{
        border-radius: 50%;
        border: solid 2px;

        margin-bottom: 1rem;
        justify-content: center;
    }
`;

export const Content = styled.div`

    padding: 5rem 1rem 5rem;
    display: flex;
    gap: 4rem;

`;


export const Pictures = styled.div`
    width: 98px;
    height: 98px;
    border: var(--purple-light) solid 3.5px;

    border-radius: 50%;
`;

export const Presentation = styled.div`
    text-align: center;
    margin-right: auto;


    background: rgba(163,96,194,0.1);


    p{
        width: 470px;
        height: 285px;
        text-align: left;
    }

`
