import styled from "styled-components"


export const Container = styled.header`
    background: var(--purple-light);
    height: 100px;
`;

export const Content = styled.div`
    max-width: 1120px;
    margin: 0 auto;

    padding: 1.5rem 1rem 0rem;
    display: flex;
    align-items: center;
    justify-content: space-between;



    a{
        color: var(--white);
    }

    h1{
        font-style: normal;
        font-weight: 700;
        font-size: 20px;
    }

`;

export const Options = styled.div`
    display: flex;
    align-items: center;
    min-width: 20px;
    gap: 3rem;

    font-style: normal;
    font-weight: 400;
    font-size: 20px;
    line-height: 24Spx;

    #bold{
        font-weight: 700;
    }


`
