import styled from "styled-components"


export const Content = styled.div`
    max-width: 1120px;
    margin: 0 auto;

    padding: 5rem 1rem 5rem;
    display: flex;


    h1, h2{
        font-style: normal;
        font-weight: 700;
        font-size: 40px;
        color: var(--blue-dark);
    }

`;

export const Presentation = styled.div`
    margin-top: 2.5rem;
    text-align: center;

    img{
        border-radius: 50%;
        width: 98px;
        height: 98px;
        border: var(--purple-light) solid 3.5px;

        margin-bottom: 1rem;
        justify-content: center;
    }

    p{
        width: 470px;
        height: 285px;
        text-align: left;
    }

`

export const Information = styled.div`
    display: grid;

`
